import { useFormik } from "formik";
import { useSubmit } from "react-use-formik";
import { useAlertContext } from "./alertContext";

const LandingSection = () => {
  const { isLoading, response, submit } = useSubmit();
  const { onOpen } = useAlertContext();

  const formik = useFormik({
    initialValues: {
      firstName: "",
      email: "",
      type: "",
      comment: "",
    },
    onSubmit: async (values, { resetForm }) => {
      try {
        isLoading(true);
        const data = await submit(values);
        if (data.type === "success") {
          onOpen({
            type: "success",
            message: `Thank you, ${values.firstName}!`,
          });
          resetForm();
        } else {
          onOpen({ type: "error", message: response.message });
        }
      } catch (error) {
        onOpen({ type: "error", message: error.message });
      } finally {
        isLoading(false);
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <label htmlFor="firstName">First Name:</label>
      <input
        type="text"
        name="firstName"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.firstName}
      />
      {formik.touched.firstName && formik.errors.firstName ? (
        <div>{formik.errors.firstName}</div>
      ) : null}
      <label htmlFor="email">Email:</label>
      <input
        type="email"
        name="email"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.email}
      />
      {formik.touched.email && formik.errors.email ? (
        <div>{formik.errors.email}</div>
      ) : null}
      <label htmlFor="type">Type:</label>
      <select
        name="type"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.type}
      >
        <option value="">Select a type</option>
        <option value="type1">Type 1</option>
        <option value="type2">Type 2</option>
      </select>
      {formik.touched.type && formik.errors.type ? (
        <div>{formik.errors.type}</div>
      ) : null}
      <label htmlFor="comment">Comment:</label>
      <textarea
        name="comment"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.comment}
      />
      {formik.touched.comment && formik.errors.comment ? (
        <div>{formik.errors.comment}</div>
      ) : null}
      <button type="submit" disabled={isLoading}>
        {isLoading ? "Loading..." : "Submit"}
      </button>
    </form>
  );
};
